<?$reqlevel=3;include("membersonly.inc.php");date_default_timezone_set('Asia/Kolkata');$cdt=date('d-M-Y');/*$file="DKM_Invoice_$cdt.xls";header("Content-type: application/vnd.ms-excel"); header("Content-Disposition: attachment; filename=$file");*/?><table border="1"><tr><th>Sl. No.</th><th>Refno.</th><th>Invoice Number</th><th>Supplier Name</th><th>Date</th><th>Amount</th></tr><?$ggttl="";$get=mysqli_query($conn,"select * from main_drcr where dldgr='12' and nrtn='Purchase' and sbill!='' and amm>0 order by sl") or die(mysqli_error($conn));$rcnt=mysqli_num_rows($get);while($row=mysqli_fetch_array($get)){	$cnt++;	$sbill=$row['sbill'];		$get1=mysqli_query($conn,"select * from main_purchase where blno='$sbill'") or die(mysqli_error($conn));	while($row1=mysqli_fetch_array($get1))	{		$inv=$row1['inv'];		$amm=$row1['amm'];		$sid=$row1['sid'];		$lcd=$row1['lcd'];		$lfr=$row1['lfr'];		$vat=$row1['vat'];				$ddt=date('d-m-Y', strtotime($row1['ddt']));				$get2=mysqli_query($conn,"select * from main_suppl where sid='$sid'") or die(mysqli_error($conn));		while($row2=mysqli_fetch_array($get2))		{			$spn=$row2['spn'];		}		}		$get3=mysqli_query($conn,"SELECT SUM(ttl) as ttl1,SUM(ppt) as ppt FROM main_purchasedet where blno='$sbill' and qty>0") or die(mysqli_error($conn));	while($row3=mysqli_fetch_array($get3))	{		$ttl1=$row3['ttl1'];		$ppt=$row3['ppt'];	}	$vatamm=0;	if($vat>0)	{	$vatamm=($ttl1*$vat)/100;	}	$gttl=($ppt+$vatamm)-$lcd-$lfr;	$ggttl=$ggttl+$gttl;	?>	<tr>	<td><?=$cnt;?></td>	<td><?=$sbill;?></td>	<td><?=$inv;?></td>	<td><?=$spn;?></td>	<td><?=$ddt;?></td>	<td align="right"><?=$gttl;?></td>	</tr>	<?}?><tr><td colspan="5">Total</td><td><?=$ggttl;?></td></tr></table>